INSERT INTO `sys_oper_log` VALUES (5191, '通知公告', 1, 'cn.edu.cidp.project.system.controller.SysNoticeController.add()', 'POST', 1, 'admin', NULL, '/system/notice', '127.0.0.1', '内网IP', '{\"noticeType\":\"1\",\"params\":{},\"noticeTitle\":\"2020年专业技术职务任职资格申报人员材料公示\",\"noticeContent\":\"<p>根据中国地震局专业技术职务任职资格评审程序，现将我局2020年度申报专业技术职务任职资格人员材料予以公示，名单见附件1（申报人员详细材料请见<a href=\\\"http://www.gsdzj.gov.cn/\\\" rel=\\\"noopener noreferrer\\\" target=\\\"_blank\\\" style=\\\"color: rgb(0, 0, 255);\\\">http://www.gsdzj.gov.cn</a>通知公告栏）：</p><p>&nbsp;&nbsp;&nbsp;&nbsp;公示期：2020年11月10日至2020年 11月16日</p><p>&nbsp;&nbsp;&nbsp;&nbsp;如对上述人员材料有异议，请在公示期内以电话、来访或书面形式向人事教育处或纪检室处反映。反映情况要客观真实，以单位名义反映情况的需加盖单位公章，以个人名义反映情况的要提供联系方式。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;监督电话：0931-8271795（人事教育处）</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;0931-8275212（纪检室）</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;附件1. 2020年专业技术职务任职资格申报人员名单</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;2. 2020年专业技术职务任职资格人员公示材料</p><p class=\\\"ql-align-justify\\\">&nbsp;</p><p class=\\\"ql-align-justify\\\">&nbsp;</p><p class=\\\"ql-align-justify\\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;人事教育处</p><p class=\\\"ql-align-justify\\\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;2020年11月9日</p><p class=\\\"ql-align-justify\\\">&nbsp;</p><p class=\\\"ql-align-justify\\\">&nbsp;</p><p class=\\\"ql-align-justify\\\">\\t附件1</p><p class=\\\"ql-align-center\\\">\\t<strong style=\\\"color: rgb(61, 61, 61);\\\">2020年专业技术职务任职资格申报人员名单</strong></p><p class=\\\"ql-align-center\\\">序</p><p class=\\\"ql-align-center\\\">号</p><p class=\\\"ql-align-center\\\">单位</p><p class=\\\"ql-align-center\\\">姓名</p><p class=\\\"ql-align-center\\\">申报资格</p><p class=\\\"ql-align-center\\\">1</p><p class=\\\"ql-align-center\\\">兰州岩土地震研究所</p><p class=\\\"ql-align-center\\\">韩昕</p><p class=\\\"ql-align-center\\\">研究实习员', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2021-05-10 09:43:49');
